        //
        hItem = WM_GetDialogItem(pMsg->hWin, ID_TEXT_IDX);
        TEXT_SetFont(hItem, &GUI_FontHZ_kaiti_20);
        TEXT_SetTextColor(hItem, GUI_BLACK);
        TEXT_SetText(hItem, "序号");
        //
