        hItem = WM_GetDialogItem(pMsg->hWin, ID_TEXT_IDX_VALUE);
        TEXT_SetFont(hItem, GUI_FONT_24_ASCII);
        TEXT_SetTextColor(hItem, GUI_BLACK);
